            ORDER BY
                m.sort_order ASC,
                m.name ASC";
            // End by Gayatri
            //print_r($query);exit;
            $statement = $em->getConnection()->prepare($query);
            $statement->execute();
            $fetchPromotiondata = $statement->fetchAll();
        }
        return $fetchPromotiondata;
    }

