        $UserEmail = $this->get('session')->get('loginCustEmail');
        if (empty($UserEmail)) {
            $UserEmail = $this->get('session')->get('loginAdminEmail');
        }
        if(!empty($EmailId)){
            $UserEmail = $EmailId;
        }
        if (empty($logModuleId) || $logModuleId == "" || $logModuleId == 0 || $logModuleId <= 0) {
            if (!empty($slug)) {
                $logModuleId = $this->getModuleIdbySlug($slug);
