        $logs->setLogFor($logFor);
        $logs->setLogType($logType);
        $logs->setLogUserId($UserId);
        $logs->setLogModuleId($logModuleId);
        $logs->setLogItemId($logItemId);
        $logs->setLogEmailId($UserEmail);
        $logs->setLogMessage($logMessage);
