                                    $ary    = explode('+', $k);
                                    $from   = date('Y');
                                    $to     = $from + (isset($ary[1]) ? $ary[1] : 10) * 1;
                                    $source = [];
                                    for ($y = $from; $y <= $to; $y++) {
                                        $source[$y] = $y;
                                    }

                                    $web['fields'][$field_name]['source'] = $source;
                                }
                            }
                            $newsource = [];
                            foreach ($web['fields'][$field_name]['source'] as $k => $v) {
                                $eval = '';
                                preg_match('/##(.*?)##/', $v, $tmp);
                                if (isset($tmp[1])) {
                                    $eval = eval('return ' . $tmp[1] . ';');
                                }

                                $k             = str_replace(@$tmp[0], $eval, $k);
                                $v             = str_replace(@$tmp[0], $eval, $v);
